//page object class -LoginPage.js
class LoginPage {
//step1-define ui elements and ui element methods
elements = {
usernameInput: () => cy.get("#username"),
passwordInput: () => cy.get("#psw"),
loginBtn: () => cy.get("#login"),
errorMessage: () => cy.get('h3[data-test="error"]'),
};
//step2-complete code for performing ui actions and verification on uielement methods 
typeUsername(username) {
this.elements.usernameInput().type(username);
}
typePassword(password) {
this.elements.passwordInput().type(password);
}
clickLogin() {
this.elements.loginBtn().click();
}
submitLogin(username,password){
this.elements.usernameInput().type(username);
this.elements.passwordInput().type(password);
this.elements.loginBtn().click();
}
}
export const loginPage = new LoginPage();
